<template>  <form class="form" action="">    <h2>Создать пост</h2>    <my-input        type="text"        v-model="post.title"        placeholder="Заголовок"    />    <my-input        type="text"        v-model="post.body"        placeholder="Текст"    />    <my-button @click.prevent="createPost">Создать</my-button>  </form></template><script>import MyInput from "@/components/UI/Input.vue";import MyButton from "@/components/UI/Button.vue";export default {  name: 'my-form',  components: {MyButton, MyInput},  data() {    return {      post: {        id: Date.now(),        title: '',        body: '',      }    }  },  methods: {    createPost() {      this.$emit('create', this.post);      this.post = {        title: '',        body: '',      }    },  }}</script><style lang="scss" scoped>  .form {    display: flex;    flex-direction: column;    row-gap: 20px;    min-width: 400px;  }</style>