<template>  <article @click="$router.push(`/posts/${post.id}`)" class="posts__item" :key="post.id">    <h2 class="posts__title">{{ post.title }}</h2>    <p class="posts__body">{{ post.body }}</p>    <p>Дата создания: {{ getDate() }}</p>    <my-button class="posts__btn" @click.stop="$emit('remove', post)">Удалить</my-button>  </article></template><script>import MyButton from "@/components/UI/Button.vue";export default {  name: 'my-post-item',  components: {MyButton},  props: {    post: {      type: [String, Number],      default: ()=> '',    }  },  methods: {    getDate() {      const date = new Date();      return `${date.getDate() <= 9 ? '0' + date.getDate() : date.getDate()}.${date.getDay() <= 9 ? '0' + date.getDay() : date.getDay()}.${date.getFullYear()}`    },  }}</script><style lang="scss" scoped>.posts__btn {  margin-top: auto;}.posts__item {  display: flex;  flex-direction: column;  justify-content: flex-start;  width: 350px;  border: 2px solid #21201F;  padding: 20px;  color: #21201F;  text-decoration: none;  transition: all .2s linear;  &:hover {    box-shadow: 0 10px 15px -5px #21201F;    transform: translateY(-10px);  }}</style>