<template>  <input      class="input"      :value="modelValue"      @input="searchQuery"      type="text"  ></template><script>export default {  name: 'my-input',  props: {    modelValue: String,  },  methods: {    searchQuery(evt) {      this.$emit('update:modelValue', evt.target.value)    }  }}</script><style lang="scss" scoped>  .input {    padding: 8px 12px;    border: 1px solid #21201F;    font-size: 16px;  }</style>