<template>  <div v-if="show" class="modal" @click="closeModal">    <div class="modal__container" @click.stop>      <slot></slot>    </div>  </div></template><script>export default {  name: 'my-modal',  props: {    show: {      type: Boolean,      default: false,    }  },  methods: {    closeModal() {      this.$emit('update:show', false)    }  }}</script><style lang="scss" scoped>  .modal {    position: fixed;    top: 0;    left: 0;    right: 0;    bottom: 0;    display: flex;    justify-content: center;    align-items: center;    background-color: rgba(0, 0, 0, 0.8);    &__container {      display: flex;      flex-direction: column;      justify-content: center;      align-items: center;      padding: 20px;      background-color: #fff;    }  }</style>